select patent_doc_id,app_no,patent_type,create_time,last_update_time,patent_name,
manual,right_claim,abstract_desc,abstract_img,patent_doc_attachment_file,contact_id,attachment_url,other_information,price from patent_documents where  patent_doc_id in

	   #{patentDocId}

SELECT p.patent_doc_id,p.app_no,p.patent_type,p.create_time,p.last_update_time,p.patent_name, p.manual,p.right_claim,p.abstract_desc,p.abstract_img,p.patent_doc_attachment_file,p.patent_doc_url, ps.patent_doc_status_id,ps.patent_doc_status_desc,pp.patent_doc_proxy_status_id,pp.patent_doc_proxy_status_desc,p.user_id,p.username FROM (SELECT pd.patent_doc_id,pd.app_no,pd.patent_type,pd.create_time,pd.last_update_time,pd.patent_name, pd.manual,pd.right_claim,pd.abstract_desc,pd.abstract_img,pd.patent_doc_attachment_file,pd.patent_doc_url,pd.patent_doc_status, pd.patent_doc_proxy_status, u.user_id,u.username FROM patent_documents pd LEFT JOIN user_patent_docs up ON up.patent_doc = pd.patent_doc_id LEFT JOIN users u ON u.user_id = up.user_id) p
LEFT JOIN patent_doc_status ps ON p.patent_doc_status =ps.patent_doc_status_id LEFT JOIN patent_doc_proxy_status pp ON p.patent_doc_proxy_status =pp.patent_doc_proxy_status_id WHERE p.patent_doc_id IN (SELECT patent_doc FROM user_patent_docs WHERE user_id = #{userId})
AND p.patent_doc_status=#{status}
AND p.patent_doc_proxy_status=#{proxyStatus}
ORDER BY create_time DESC ,last_update_time DESC limit #{startIndex}, #{pageSize}

SELECT count(*)
FROM patent_documents p LEFT JOIN patent_doc_status ps ON p.patent_doc_status =ps.patent_doc_status_id
LEFT JOIN patent_doc_proxy_status pp ON p.patent_doc_proxy_status =pp.patent_doc_proxy_status_id
WHERE p.patent_doc_id IN (SELECT patent_doc FROM user_patent_docs WHERE user_id = #{userId}) 

	AND p.patent_doc_status=#{status}


	AND p.patent_doc_proxy_status=#{proxyStatus}

ORDER BY create_time DESC ,last_update_time DESC limit #{startIndex}, #{pageSize}

