mapper.select:
		 SELECT  p.patent_type patentType, COUNT(*) patentCount
 		FROM patents p LEFT JOIN patent_status ps ON p.patent_status = ps.patent_status_id JOIN user_patents up ON p.patent_id = up.patent
		WHERE up.user = #{userId} and up.trash_status=1 GROUP BY p.patent_type;

		 SELECT  p.patent_status patentStatus,COUNT(*) patentCount
 		FROM patents p LEFT JOIN patent_status ps ON p.patent_status = ps.patent_status_id JOIN user_patents up ON p.patent_id = up.patent
		WHERE up.user = #{userId} and up.trash_status=1 GROUP BY p.patent_status;




SELECT usp.*,u.user_id,u.username FROM 
	(SELECT sg.patent_id,sg.price,sg.status,sg.transaction_type,sg.add_date, sg.transaction_date,sg.transferor,p.patent_name,p.app_no,
	   sg.recommend_status,ss.patent_first_column_id, sg.patent_second_column,pt.patent_type_id,pt.patent_type_desc,p.app_person,
	   p.patent_status_text,sg.description
	FROM  sell_patent_goods sg LEFT JOIN patents p ON sg.patent_id = p.patent_id 
	LEFT JOIN patent_status ps ON p.patent_status = ps.patent_status_id  
	LEFT JOIN patent_types pt ON p.patent_type = pt.patent_type_id  
	LEFT JOIN sell_second_column  ss ON ss.patent_second_column_id=sg.patent_second_column 
	WHERE  OWNER=#{userId}

 			and pt.patent_type_id = #{patentType}


	 		and ps.patent_status_id = #{patentStatus}


 			and sg.status = #{status}


	 		and sg.transaction_type = #{transactionType}


	 		and sg.add_date >= #{startAddDate}


	 		 and sg.add_date <= #{endAddDate} 


			and (p.app_no like #{keyword_pattern} or p.patent_name like #{keyword_pattern} or sg.description like #{keyword_pattern})




					and ss.patent_first_column_id=#{firstColumnId} and sg.patent_second_column=#{secondColumnId}


				and ss.patent_first_column_id=#{firstColumnId}



	ORDER BY sg.status ASC,sg.add_date DESC limit #{page.startIndex}, #{page.pageSize}
	) usp
	LEFT JOIN user_patents up ON usp.patent_id=up.patent 
	LEFT JOIN users u ON up.user=u.user_id




SELECT count(*)
FROM
(SELECT  sg.patent_id,sg.price,sg.status,sg.transaction_type,sg.add_date,sg.owner,sg.transaction_date,sg.transferor,p.patent_name,p.app_no,ss.patent_first_column_id,sg.patent_second_column,sg.description,pt.patent_type_id,pt.patent_type_desc,ps.patent_status_id, ps.patent_status_desc
FROM  sell_patent_goods sg LEFT JOIN patents p ON sg.patent_id = p.patent_id left JOIN sell_second_column  ss ON ss.patent_second_column_id=sg.patent_second_column LEFT JOIN patent_types pt ON p.patent_type = pt.patent_type_id left join patent_status ps on p.patent_status = ps.patent_status_id) up
WHERE up.owner=#{userId}

 			and up.patent_type_id = #{patentType}


	 		and up.patent_status_id = #{patentStatus}


 			and up.status = #{status}


	 		and up.transaction_type = #{transactionType}


	 		and up.add_date >= #{startAddDate}


	 		 and up.add_date <= #{endAddDate} 


			and (up.app_no like #{keyword_pattern} or up.patent_name like #{keyword_pattern} or up.description like #{keyword_pattern})




					and up.patent_first_column_id=#{firstColumnId} and up.patent_second_column=#{secondColumnId}


				and up.patent_first_column_id=#{firstColumnId}




SELECT usp.*,u.user_id,u.username FROM 
	(SELECT sg.patent_id,sg.price,sg.status,sg.transaction_type,sg.add_date, sg.transaction_date,sg.transferor,p.patent_name,p.app_no,
	   sg.recommend_status,ss.patent_first_column_id, sg.patent_second_column,pt.patent_type_id,pt.patent_type_desc,p.app_person,
	   p.patent_status_text,sg.description
	FROM  sell_patent_goods sg LEFT JOIN patents p ON sg.patent_id = p.patent_id 
	LEFT JOIN patent_status ps ON p.patent_status = ps.patent_status_id  
	LEFT JOIN patent_types pt ON p.patent_type = pt.patent_type_id  
	LEFT JOIN sell_second_column  ss ON ss.patent_second_column_id=sg.patent_second_column 
	WHERE  1=1

 			and pt.patent_type_id = #{patentType}


	 		and ps.patent_status_id = #{patentStatus}


 			and sg.status = #{status}


	 		and sg.transaction_type = #{transactionType}


	 		and sg.add_date >= #{startAddDate}


	 		 and sg.add_date <= #{endAddDate} 


			and (p.app_no like #{keyword_pattern} or p.patent_name like #{keyword_pattern} or sg.description like #{keyword_pattern})




					and ss.patent_first_column_id=#{firstColumnId} and sg.patent_second_column=#{secondColumnId}


				and ss.patent_first_column_id=#{firstColumnId}



	ORDER BY sg.status ASC,sg.add_date DESC limit #{page.startIndex}, #{page.pageSize}
	) usp
	LEFT JOIN user_patents up ON usp.patent_id=up.patent 
	LEFT JOIN users u ON up.user=u.user_id




SELECT count(*)
FROM
(SELECT  sg.patent_id,sg.price,sg.status,sg.transaction_type,sg.add_date,sg.owner,sg.transaction_date,sg.transferor,p.patent_name,p.app_no,ss.patent_first_column_id,sg.patent_second_column,sg.description,pt.patent_type_id,pt.patent_type_desc,ps.patent_status_id, ps.patent_status_desc
FROM  sell_patent_goods sg LEFT JOIN patents p ON sg.patent_id = p.patent_id left JOIN sell_second_column  ss ON ss.patent_second_column_id=sg.patent_second_column LEFT JOIN patent_types pt ON p.patent_type = pt.patent_type_id left join patent_status ps on p.patent_status = ps.patent_status_id) up
WHERE 1=1

 			and up.patent_type_id = #{patentType}


	 		and up.patent_status_id = #{patentStatus}


 			and up.status = #{status}


	 		and up.transaction_type = #{transactionType}


	 		and up.add_date >= #{startAddDate}


	 		 and up.add_date <= #{endAddDate} 


			and (up.app_no like #{keyword_pattern} or up.patent_name like #{keyword_pattern} or up.description like #{keyword_pattern})




					and up.patent_first_column_id=#{firstColumnId} and up.patent_second_column=#{secondColumnId}


				and up.patent_first_column_id=#{firstColumnId}

